public class threeMethod {
	
	public static void sum (int num1 , int num2)
	{
		int z = num1 + num2;
		result(z);
		
	}
	public static void result (int m)
	{
		System.out.println(m);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		sum(10,20);
		
	}

}
